import type { ReferenceOptions } from './Property';
import type { AnyEntity, AnyString, EntityName } from '../typings';
export declare function ManyToOne<T, O>(entity?: ManyToOneOptions<T, O> | string | ((e?: any) => EntityName<T>), options?: Partial<ManyToOneOptions<T, O>>): (target: AnyEntity, propertyName: string) => any;
export interface ManyToOneOptions<T, O> extends ReferenceOptions<T, O> {
    inversedBy?: (string & keyof T) | ((e: T) => any);
    /** @deprecated use `ref` instead, `wrappedReference` option will be removed in v6 */
    wrappedReference?: boolean;
    ref?: boolean;
    primary?: boolean;
    mapToPk?: boolean;
    joinColumn?: string;
    joinColumns?: string[];
    referenceColumnName?: string;
    referencedColumnNames?: string[];
    onDelete?: 'cascade' | 'no action' | 'set null' | 'set default' | AnyString;
    onUpdateIntegrity?: 'cascade' | 'no action' | 'set null' | 'set default' | AnyString;
}
